@@ -0,0 +1,149 @@
+const path = require("path");
+const fs = require("fs");
+const multer = require("multer");
+const jwt = require("jsonwebtoken");
+const User = require("../models/models");
+
+const uploadDir = path.join(__dirname, "..", "uploads");
+if (!fs.existsSync(uploadDir)) {
+  fs.mkdirSync(uploadDir, { recursive: true });
+}
+
+const storage = multer.diskStorage({
+  destination: (req, file, cb) => {
+    cb(null, uploadDir);
+  },
+  filename: (req, file, cb) => {
+    const ext = path.extname(file.originalname);
+    const base = path.basename(file.originalname, ext);
+    cb(null, `${base}-${Date.now()}${ext}`);
+  },
+});
+
+const fileFilter = (req, file, cb) => {
+  const allowedTypes = ["image/jpeg", "image/png", "application/pdf"];
+  if (allowedTypes.includes(file.mimetype)) {
+    cb(null, true);
+  } else {
+    cb(new multer.MulterError("LIMIT_UNEXPECTED_FILE", "Invalid file type."));
+  }
+};
+
+const upload = multer({ storage, fileFilter }).single("file");
+
+exports.uploadFile = (req, res) => {
+  upload(req, res, async (err) => {
+    if (err) {
+      if (err instanceof multer.MulterError) {
+        return res.status(400).json({ message: "Multer error", error: err.message });
+      }
+      return res.status(400).json({ message: "Upload error", error: err.message });
+    }
+
+    if (!req.file) {
+      return res.status(400).json({ message: "No file uploaded" });
+    }
+
+    try {
+      const authHeader = req.headers.authorization || "";
+      const token = authHeader.split(" ")[1];
+      if (!token) return res.status(401).json({ message: "Unauthorized: No token provided" });
+
+      const decoded = jwt.verify(token, process.env.JWT_SECRET);
+      const userId = decoded.id;
+
+      const user = await User.findById(userId);
+      if (!user) return res.status(404).json({ message: "User not found" });
+
+      const { label, documentType, type } = req.body;
+
+      if (type === "profileImage") {
+        user.profileImage = `/uploads/${req.file.filename}`;
+      } else {
+        if (!user.documentRequirements) user.documentRequirements = {};
+
+        switch (documentType) {
+          case "politicalDeclaration":
+            user.documentRequirements.politicalDeclaration = `/uploads/${req.file.filename}`;
+            break;
+          case "witnessTestimonies":
+            user.documentRequirements.witnessTestimonies = [`/uploads/${req.file.filename}`];
+            break;
+          case "idDocument":
+            user.documentRequirements.idDocumentFile = `/uploads/${req.file.filename}`;
+            break;
+          default:
+            return res.status(400).json({ message: "Invalid document type" });
+        }
+      }
+
+      await user.save();
+
+      res.status(200).json({
+        message: "File uploaded and user data updated successfully",
+        filePath: `/uploads/${req.file.filename}`,
+      });
+    } catch (error) {
+      console.error("Server error in uploadFile:", error);
+      res.status(500).json({ message: "Server error", error: error.message });
+    }
+  });
+};
+
+exports.deleteDocument = async (req, res) => {
+  try {
+    const { documentType } = req.body;
+
+    const authHeader = req.headers.authorization || "";
+    const token = authHeader.split(" ")[1];
+    if (!token) return res.status(401).json({ message: "Unauthorized: No token provided" });
+
+    const decoded = jwt.verify(token, process.env.JWT_SECRET);
+    const userId = decoded.id;
+
+    const user = await User.findById(userId);
+    if (!user) return res.status(404).json({ message: "User not found" });
+
+    if (!user.documentRequirements) return res.status(400).json({ message: "No documents to delete" });
+
+    switch (documentType) {
+      case "politicalDeclaration":
+        user.documentRequirements.politicalDeclaration = undefined;
+        break;
+      case "witnessTestimonies":
+        user.documentRequirements.witnessTestimonies = [];
+        break;
+      case "idDocument":
+        user.documentRequirements.idDocumentFile = undefined;
+        break;
+      default:
+        return res.status(400).json({ message: "Invalid document type" });
+    }
+
+    await user.save();
+
+    res.status(200).json({ message: `${documentType} deleted successfully` });
+  } catch (error) {
+    console.error("Server error in deleteDocument:", error);
+    res.status(500).json({ message: "Server error", error: error.message });
+  }
+};
+
+// List users with all docs completed (public view)
+exports.publicView = async (req, res) => {
+  try {
+    // Find users with all three required documents uploaded (non-null/non-empty)
+    const users = await User.find({
+      $and: [
+        { "documentRequirements.politicalDeclaration": { $exists: true, $ne: null } },
+        { "documentRequirements.witnessTestimonies.0": { $exists: true } },
+        { "documentRequirements.idDocumentFile": { $exists: true, $ne: null } },
+      ],
+    }).select("fullNames familyName");
+
+    res.json(users);
+  } catch (error) {
+    console.error("Error in publicView:", error);
+    res.status(500).json({ message: "Server error", error: error.message });
+  }
+};

