+const mongoose = require("mongoose");
+const bcrypt = require("bcryptjs");
+
+// ðŸ“„ Subschema for 528-specific document verification
+const documentSchema = new mongoose.Schema(
+  {
+    politicalDeclaration: { type: String },
+    witnessTestimonies: [{ type: String }],
+    idDocument: {
+      type: String,
+      enum: ["DriverLicense", "Passport", "NationalID"],
+    },
+    idDocumentFile: { type: String },
+    photoWithWhiteBg: { type: String },
+    redThumbprint: { type: String },
+    utilityBill: { type: String },
+    isValid: { type: Boolean, default: false },
+  },
+  { _id: false }
+);
+
+// ðŸ‘¤ Main user schema
+const userSchema = new mongoose.Schema(
+  {
+    fullNames: {
+      type: String,
+      required: [true, "Full names are required"],
+      trim: true,
+    },
+    familyName: {
+      type: String,
+      required: [true, "Family name is required"],
+      trim: true,
+    },
+    email: {
+      type: String,
+      required: [true, "Email is required"],
+      unique: true,
+      lowercase: true,
+      trim: true,
+    },
+    password: {
+      type: String,
+      required: [true, "Password is required"],
+      minlength: 6,
+    },
+    gfgNumber: {
+      type: String,
+      required: [true, "GFG number is required"],
+    },
+    documentLabel: {
+      type: String,
+      enum: ["528", "928", "Praecipe"],
+      required: [true, "Document label is required"],
+    },
+    documentRequirements: {
+      type: documentSchema,
+      required: function () {
+        return this.documentLabel === "528";
+      },
+    },
+  },
+  { timestamps: true }
+);
+
+// ðŸ”’ Hash password and GFG number before saving
+userSchema.pre("save", async function (next) {
+  try {
+    if (this.isModified("password")) {
+      const salt = await bcrypt.genSalt(10);
+      this.password = await bcrypt.hash(this.password, salt);
+    }
+
+    if (this.isModified("gfgNumber")) {
+      const salt = await bcrypt.genSalt(10);
+      this.gfgNumber = await bcrypt.hash(this.gfgNumber, salt);
+    }
+
+    next();
+  } catch (err) {
+    next(err);
+  }
+});
+
+// ðŸ§  Instance methods
+userSchema.methods.comparePassword = async function (candidatePassword) {
+  return bcrypt.compare(candidatePassword, this.password);
+};
+
+userSchema.methods.compareGfgNumber = async function (candidateGfgNumber) {
+  return bcrypt.compare(candidateGfgNumber, this.gfgNumber);
+};
+
+module.exports = mongoose.model("User", userSchema);
+ const jwt = require("jsonwebtoken");
+const User = require("../models/models");
+
+const authMiddleware = async (req, res, next) => {
+  let token;
+  
+  if (req.headers.authorization && req.headers.authorization.startsWith("Bearer")) {
+    try {
+      // Get token from header
+      token = req.headers.authorization.split(" ")[1];
+      
+      // Verify token
+      const decoded = jwt.verify(token, process.env.JWT_SECRET || "your_jwt_secret");
+      
+      // Get user from token
+      req.user = await User.findById(decoded.id).select("-password -gfgNumber");
+      
+      next();
+    } catch (error) {
+      console.error(error);
+      res.status(401).json({ message: "Not authorized" });
+    }
+  }
+  
+  if (!token) {
+    res.status(401).json({ message: "Not authorized, no token" });
+  }
+};
+
+module.exports = authMiddleware; 
