@@ -0,0 +1,71 @@
+const mongoose = require("mongoose");
+const bcrypt = require("bcryptjs");
+
+// Subschema for required document verification (for 528 users)
+const documentSchema = new mongoose.Schema({
+  politicalDeclaration: { type: String, required: true }, // File path or URL
+  witnessTestimonies: [{ type: String, required: true }], // Array of 3 file paths
+  idDocument: {
+    type: String,
+    enum: ["DriverLicense", "Passport", "NationalID"],
+    required: true,
+  },
+  idDocumentFile: { type: String, required: true }, // File path or URL
+  photoWithWhiteBg: { type: String, required: true }, // File path
+  redThumbprint: { type: String, required: true }, // File path
+  utilityBill: { type: String, required: true }, // File path
+  isValid: { type: Boolean, default: false }, // ‚úÖ Tick green if complete
+}, { _id: false });
+
+// Main User schema
+const userSchema = new mongoose.Schema({
+  fullNames: { type: String, required: true },
+  familyName: { type: String, required: true },
+  email: { type: String, required: true, unique: true },
+  password: { type: String, required: true },
+  gfgNumber: { type: String, required: true },
+  documentLabel: {
+    type: String,
+    enum: ["528", "928", "Praecipe"],
+    required: true,
+  },
+  documentRequirements: {
+    type: documentSchema,
+    required: function () {
+      return this.documentLabel === "528";
+    },
+  },
+}, { timestamps: true });
+
+// üîí Hash password and gfgNumber before saving
+userSchema.pre("save", async function (next) {
+  try {
+    // Hash password if modified
+    if (this.isModified("password")) {
+      const salt = await bcrypt.genSalt(10);
+      this.password = await bcrypt.hash(this.password, salt);
+    }
+
+    // Hash gfgNumber if modified
+    if (this.isModified("gfgNumber")) {
+      const salt = await bcrypt.genSalt(10);
+      this.gfgNumber = await bcrypt.hash(this.gfgNumber, salt);
+    }
+
+    next();
+  } catch (err) {
+    next(err);
+  }
+});
+
+// üîê Compare password
+userSchema.methods.comparePassword = async function (candidatePassword) {
+  return bcrypt.compare(candidatePassword, this.password);
+};
+
+// üîê Compare gfgNumber
+userSchema.methods.compareGfgNumber = async function (candidateGfgNumber) {
+  return bcrypt.compare(candidateGfgNumber, this.gfgNumber);
+};
+
+module.exports = mongoose.model("User", userSchema);

