const express = require("express");
const router = express.Router();
const { 
  registerUser, 
  loginUser,
  getUserData,
  updateUser,
  uploadProfilePic,
  changePassword,
  logoutUser
} = require("../controllers/authController");
const authMiddleware = require("../middleware/authMiddleware");
const { profilePicUpload } = require("../middleware/upload");

// POST /api/auth/register - Register a new user
router.post("/register", registerUser);

// POST /api/auth/login - Login user
router.post("/login", loginUser);

// POST /api/auth/logout - Logout user
router.post("/logout", authMiddleware, logoutUser);

// GET /api/auth/me - Get current user data
router.get("/me", authMiddleware, getUserData);

// PUT /api/auth/update - Update user data
router.put("/update", authMiddleware, updateUser);

// PUT /api/auth/change-password - Change user password
router.put("/change-password", authMiddleware, changePassword);

// POST /api/auth/profile-pic - Upload profile picture
router.post("/profile-pic", authMiddleware, profilePicUpload, uploadProfilePic);

module.exports = router;
