const express = require("express");
const cors = require("cors");
const dotenv = require("dotenv");
const connectDB = require("./config/db");
const authRoutes = require("./routes/auth");
const homeRoutes = require("./routes/home"); // Home routes (upload, delete)
const path = require("path");
const fs = require("fs");

dotenv.config(); // Load environment variables

// Connect to MongoDB
connectDB();

const app = express();
const PORT = process.env.PORT || 5000;

// Ensure uploads directory exists
const uploadsDir = path.join(__dirname, "uploads");
if (!fs.existsSync(uploadsDir)) {
  fs.mkdirSync(uploadsDir);
}

// Middleware
app.use(cors()); // Enable CORS
app.use(express.json()); // Parse incoming JSON
app.use(express.urlencoded({ extended: true })); // Parse urlencoded form data

// Static folder for uploads so files can be accessed publicly
app.use("/uploads", express.static(uploadsDir));

// Routes
app.use("/api/auth", authRoutes);
app.use("/api/home", homeRoutes); // Home routes with upload and delete

// Root route for testing
app.get("/", (req, res) => {
  res.send("Backend is running...");
});

// Handle invalid routes
app.use((req, res) => {
  res.status(404).json({ message: "Route not found" });
});

// Global error handler
app.use((err, req, res, next) => {
  console.error(err.stack);
  res.status(500).json({ message: "Internal Server Error" });
});

// Start server
app.listen(PORT, () => {
  console.log(`âœ… Server running on http://localhost:${PORT}`);
});
