/* Container shrink & responsive */
.home-wrapper {
  padding: 1rem;
  transition: margin-right 0.3s ease;
  max-width: 600px;
  margin: 0 auto;
}

/* Push left when sidebar open */
.home-wrapper.sidebar-open {
  margin-right: 250px;
}

/* Upload button styling */
.upload-container {
  position: relative;
  width: 100%;
  max-width: 320px;
  margin: 2rem auto;
}

.upload-btn {
  width: 100%;
  margin-bottom: 400%;
  padding: 0.7rem 1rem;
  font-size: 1.1rem;
  background-color: #2f80ed;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
}

.upload-btn:hover {
  background-color: #1c5ed8;
}

/* Dropdown menu */
.dropdown-menu {
  position: absolute;
  margin-bottom: 110%;
  left: 0;
  width: 180%;
  background: #222;
  border-radius: 8px;
  box-shadow: 0 5px 10px rgba(0,0,0,0.3);
  z-index: 10;
  padding: 1rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

/* Each item in dropdown */
.dropdown-item {
  position: relative;
}

/* Tick mark */
.tick {
  position: absolute;
  top: 8px;
  right: 8px;
  font-size: 1.5rem;
  color: #00ff00;
  font-weight: bold;
}

/* File upload smaller style */
.custum-file-upload {
  height: 120px;
  width: 100%;
  max-width: 280px;
  display: flex;
  flex-direction: column;
  gap: 12px;
  cursor: pointer;
  align-items: center;
  justify-content: center;
  border: 2px dashed #fa6c00;
  background-color: transparent;
  padding: 1rem;
  border-radius: 10px;
  box-shadow: none;
  color: #f76700;
  transition: border-color 0.3s ease, color 0.3s ease;
}

.custum-file-upload:hover {
  border-color: #2f80ed;
  color: #2f80ed;
}

.custum-file-upload .icon svg {
  height: 50px;
  fill: #ff5500;
}

/* Text */
.custum-file-upload .text span {
  font-weight: 400;
  font-size: 1rem;
}

/* Hide native input */
.custum-file-upload input {
  display: none;
}

/* Sidebar toggle circle or profile image */
.sidebar-toggle {
  position: fixed;
  top: 20px;
  right: 20px;
  background: #2f80ed;
  color: white;
  font-weight: bold;
  font-size: 20px;
  border-radius: 50%;
  width: 36px;
  height: 36px;
  line-height: 36px;
  text-align: center;
  cursor: pointer;
  z-index: 100;
  user-select: none;
  box-shadow: 0 2px 6px rgba(0,0,0,0.3);
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

/* Profile image inside sidebar toggle */
.sidebar-toggle .profile-img {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  object-fit: cover;
  display: block;
}

/* Sidebar */
.sidebar {
  position: fixed;
  top: 0;
  right: 0;
  width: 250px;
  height: 100vh;
  background: #222;
  color: #eee;
  padding: 1.5rem;
  box-shadow: -3px 0 8px rgba(0,0,0,0.4);
  transition: transform 0.3s ease;
  z-index: 99;
  overflow-y: auto;
  transform: translateX(100%);
  box-sizing: border-box;
}

.sidebar.open {
  transform: translateX(0);
}

.sidebar-content {
  display: flex;
  flex-direction: column;
  min-height: 100%;
  padding-bottom: 20px;
}

.sidebar h2 {
  margin: 0 0 1rem 0;
  font-size: 1.5rem;
  line-height: 1.3;
}

/* Back button */
.back-btn {
  background: none;
  border: none;
  color: #2f80ed;
  font-size: 1rem;
  margin: 0 0 1.5rem 0;
  cursor: pointer;
  padding: 0.5rem 0;
  font-weight: bold;
  display: inline-flex;
  align-items: center;
  width: auto;
}

.back-btn:hover {
  text-decoration: underline;
}

/* Editable fields */
.editable-field {
  margin-bottom: 1.5rem;
  display: flex;
  flex-wrap: wrap;
  width: 100%;
  gap: 0.5rem;
}

.editable-field label {
  width: 100%;
  font-weight: bold;
  user-select: none;
  font-size: 0.9rem;
}

.editable-field span {
  flex: 1;
  min-width: 0;
  word-break: break-word;
  overflow-wrap: anywhere;
}

.editable-field input {
  width: 100%;
  padding: 0.5rem;
  border-radius: 4px;
  border: 1px solid #ccc;
  font-size: 0.95rem;
  box-sizing: border-box;
  background: #333;
  color: #fff;
}

.edit-btn {
  background: none;
  border: none;
  color: #2f80ed;
  cursor: pointer;
  font-size: 1.1rem;
  user-select: none;
  padding: 0.3rem;
  margin-left: auto;
  align-self: flex-start;
}

.edit-btn:hover {
  text-decoration: underline;
}

.save-section {
  margin-top: auto;
  padding: 1rem 0 0;
  width: 100%;
}

.save-btn {
  background-color: #4CAF50;
  color: white;
  border: none;
  padding: 0.75rem;
  border-radius: 4px;
  cursor: pointer;
  font-size: 1rem;
  transition: background-color 0.3s;
  width: 100%;
}

/* Profile upload area */
.profile-upload {
  margin: 0 auto 1.5rem;
  cursor: pointer;
  width: 120px;
  height: 120px;
  border-radius: 50%;
  border: 2px dashed #2f80ed;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  background-color: #f0f0f0;
  color: #555;
  font-size: 0.9rem;
  text-align: center;
  padding: 0.5rem;
  box-sizing: border-box;
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .sidebar {
    width: 100%;
    padding: 1.25rem;
  }

  .home-wrapper.sidebar-open {
    margin-right: 0;
    transform: translateX(-100%);
  }

  .sidebar-content {
    padding-bottom: 1rem;
  }

  .profile-upload {
    width: 100px;
    height: 100px;
    font-size: 0.8rem;
  }

  .editable-field {
    margin-bottom: 1.25rem;
  }
}

/* Add to your Home.css */
.user-info {
  display: flex;
  align-items: center;
  gap: 10px;
}

.user-avatar {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  object-fit: cover;
}

.completed-users-list {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.completed-user {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  background: #f5f5f5;
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.2s;
}

.completed-user:hover {
  background: #e0e0e0;
}

.completed-badge {
  background: #4CAF50;
  color: white;
  padding: 3px 8px;
  border-radius: 10px;
  font-size: 0.8rem;
}

.loading-spinner {
  border: 3px solid #f3f3f3;
  border-top: 3px solid #3498db;
  border-radius: 50%;
  width: 20px;
  height: 20px;
  animation: spin 1s linear infinite;
  margin: 0 auto;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@media (max-width: 480px) {
  .sidebar {
    padding: 1rem;
  }

  .sidebar h2 {
    font-size: 1.3rem;
    margin-bottom: 0.75rem;
  }

  .back-btn {
    margin-bottom: 1rem;
    font-size: 0.9rem;
  }

  .editable-field {
    gap: 0.3rem;
  }

  .editable-field label {
    font-size: 0.85rem;
  }

  .editable-field input {
    padding: 0.4rem;
    font-size: 0.9rem;
  }

  .profile-upload {
    width: 80px;
    height: 80px;
    margin-bottom: 1rem;
  }

  .save-btn {
    padding: 0.6rem;
    font-size: 0.9rem;
  margin-top: 0.5rem;
  }
}
